"""
    Name        : Qakbot decode string
    Author      : Charles Lomboni
    Description : Binary Ninja plugin to decode strings from Qakbot sample
"""

xor_position = "3B7397BE28A1C5F978B59AB2051D03958DF21305135685E5460321F2A9E6606065A8F68BE82962667C5FEB26A23B607EA9220EA63E6F5F043DF1ECACB21F1869"
heap_allocate = ""

def get_ref_from_func():
    ref_lst = []
    fn_name = TextLineField("What is the func name? ")
    get_form_input(["Get Function Name", None, fn_name], "Decode Qakbot strings")
    
    fn_addr = bv.get_functions_by_name(fn_name.result)[0].start
    
    for x in bv.get_callers(fn_addr):
        ref_lst.append(x)
    
    return ref_lst

def get_encoded_strs(refs):
    encoded_strs = []
    for x in refs:
    	splited_value = str(x.hlil).split('0x')
    	if len(splited_value) > 1 and len(splited_value[1].replace(')','')) <= 4:
            encoded_strs.append(f"{x.address};0x{splited_value[1].replace(')','')}")
    
    return encoded_strs

def decode_str(eax_value):
    decoded_char = "xx"
    lst_str = []

    while(decoded_char != "\x00"):
        dl = eax_value & 0x3F
        dl = xor_position[dl*2:dl*2+2]
        decoded_char = chr(int(heap_allocate[eax_value*2:eax_value*2+2], 16) ^ int(dl, 16))
        lst_str.append(decoded_char)
        eax_value = eax_value + 1

    return ''.join(lst_str)

refs = get_ref_from_func()
enc_strs = get_encoded_strs(refs)

# mw_decrypt_string
for x in enc_strs:
    splited = x.split(';')
    decoded_str = decode_str(int(splited[1],16))
    print(f"[+] {hex(int(splited[0]))} => {decoded_str}")
    bv.set_comment_at(int(splited[0]), decoded_str)